import React from 'react'
import { DataGrid } from '@mui/x-data-grid';
import { DeleteForever } from "@mui/icons-material";

const relevantArticle = () => {
    const columns = [
        { field: 'id', headerName: 'ID', width: 70 },
        { field: 'title', headerName: 'Title', width: 130 , renderCell: (params)=> {
            return(
                <div className="title">
                    {params.row.userName}
                </div>
            )
        }},
        {
            field: 'text',
            headerName: 'Text',
            description: 'Here is the text of the article.',
            sortable: false,
            width: 160,
        },
        {
            field: 'author',
            headerName: 'Author',
            description: 'Author of the Article.',
            sortable: false,
            width: 160,
        },
        {
            field: 'blog',
            headerName: 'Blog',
            description: 'Name of the blog.',
            sortable: false,
            width: 160,
        },
        {
            field: 'url',
            headerName: 'URL',
            description: 'URL of the blog article.',
            sortable: false,
            width: 160,
        },
        {
            field: "action",
            headerName: "Action",
            width: 150,
            renderCell: (params)=>{
                return(
                    <>
                        <button className="userListEdit">Edit</button>
                        <DeleteForever />
                    </>
                );
            },
        },
      ];

    return(
        <div className="relevantArticles">
            <DataGrid
                rows={rows}
                columns={columns}
                pageSize={5}
                rowsPerPageOptions={[5]}
                checkboxSelection
            />
        </div>
    )
}

export default relevantArticle
